-- ----------------------------
-- View structure for blogs_with_category_name
-- ----------------------------
DROP VIEW IF EXISTS `blogs_with_category_name`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `blogs_with_category_name` AS select blogs.*, c.name as category_name
from blogs
join blog_categories as c on c.id = blogs.category_id ;

-- ----------------------------
-- View structure for blogs_with_comment_counts
-- ----------------------------
DROP VIEW IF EXISTS `blogs_with_comment_counts`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `blogs_with_comment_counts` AS SELECT
	bc.*,
	cc.counts AS comment_counts 
FROM
	blogs_with_category_name as bc
	LEFT JOIN comment_counts cc ON cc.blog_id = bc.id ;

-- ----------------------------
-- View structure for categories_with_blog_counts
-- ----------------------------
DROP VIEW IF EXISTS `categories_with_blog_counts`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `categories_with_blog_counts` AS select category_id, count(*) as counts
from blogs
WHERE permission = 1
GROUP BY category_id ;

-- ----------------------------
-- View structure for category_info
-- ----------------------------
DROP VIEW IF EXISTS `category_info`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `category_info` AS select bc.*, c.counts as counts
from blog_categories as bc
join categories_with_blog_counts as c on c.category_id = bc.id ;

-- ----------------------------
-- View structure for comments_with_user
-- ----------------------------
DROP VIEW IF EXISTS `comments_with_user`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `comments_with_user` AS SELECT comments.*, users.avatar, users.name
FROM comments
JOIN users ON users.id = comments.user_id ;

-- ----------------------------
-- View structure for comment_counts
-- ----------------------------
DROP VIEW IF EXISTS `comment_counts`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `comment_counts` AS SELECT
	blog_id,
	count(*) AS counts 
FROM
	comments 
GROUP BY
	blog_id ;

